final List<dynamic> styles = ['/*\nbody {\n  font-family: sans-serif;\n  font-size: 15px;\n  overflow: auto;\n}\n*/\n.tree {\n  height: calc(100vh - 64px);\n  overflow: auto;\n}\n\n.tree ul {\n  position: relative;\n  padding: 1em 0;\n  white-space: nowrap;\n  margin: 0 auto;\n  text-align: center;\n}\n.tree ul::after {\n  content: "";\n  display: table;\n  clear: both;\n}\n\n.tree li {\n  display: inline-block;\n  vertical-align: top;\n  text-align: center;\n  list-style-type: none;\n  position: relative;\n  padding: 1em 0.5em 0 0.5em;\n}\n.tree li::before, .tree li::after {\n  content: "";\n  position: absolute;\n  top: 0;\n  right: 50%;\n  border-top: 1px solid #ccc;\n  width: 50%;\n  height: 1em;\n}\n.tree li::after {\n  right: auto;\n  left: 50%;\n  border-left: 1px solid #ccc;\n}\n.tree li:only-child::after, .tree li:only-child::before {\n  display: none;\n}\n.tree li:only-child {\n  padding-top: 0;\n}\n.tree li:first-child::before, .tree li:last-child::after {\n  border: 0 none;\n}\n.tree li:last-child::before {\n  border-right: 1px solid #ccc;\n  border-radius: 0 5px 0 0;\n}\n.tree li:first-child::after {\n  border-radius: 5px 0 0 0;\n}\n\n.tree ul ul::before {\n  content: "";\n  position: absolute;\n  top: 0;\n  left: 50%;\n  border-left: 1px solid #ccc;\n  width: 0;\n  height: 1em;\n}\n\n.tree li a {\n  border: 1px solid #ccc;\n  /* padding: .5em .75em; */\n  padding: 0 0.25em;\n  text-decoration: none;\n  display: inline-block;\n  border-radius: 5px;\n  color: #333;\n  position: relative;\n  top: 1px;\n}\n\n.tree li a:hover,\n.tree li a:hover + ul li a {\n  background: #e9453f;\n  color: #fff;\n  border: 1px solid #e9453f;\n}\n\n.tree li a:hover + ul li::after,\n.tree li a:hover + ul li::before,\n.tree li a:hover + ul::before,\n.tree li a:hover + ul ul::before {\n  border-color: #e9453f;\n}\n\n/*\n.text-group-letter {\n    color: white;\n    mix-blend-mode: difference;\n    line-height: 40px;\n}\n*/\n.custom-row {\n  display: inline-block;\n  vertical-align: middle;\n  height: 1em;\n}\n\n.custom-name {\n  min-width: 150px;\n  max-width: 400px;\n  /* width: 400px; */\n  text-align: left;\n  height: 100%;\n}\n\n.thin {\n  margin: 0;\n  height: 1em;\n}\n\n.pointer {\n  cursor: pointer;\n}\n\n.vertical-group-leader {\n  display: inline-block;\n}\n'];