final List<dynamic> styles = ['/*\n// Copyright (c) 2018, Levius Tecnologia Ltda. All rights reserved.\n// Author: Samuel C. Schwebel.\n*/\n\nbody {\n /* max-width: 800px; */\n  margin: 0 auto;\n  padding: 0vw;\n}\n\n* {\n  font-family: Roboto, Helvetica, Arial, sans-serif;\n}\n/*\n.shadow-box {\n  @include material-shadow();\n}\n*/\n.container {\n  display: flex;\n  justify-content: center;\n}\n\n.container-list {\n\n  /* centering inner elements */\n  margin-left: auto;\n  margin-right: auto;\n\n  /* material design specification - large = initial */\n  max-width: 1600px; /*1280px*/\n  width: 100%;\n\n  padding-top: 8px;\n  padding-bottom: 8px;\n\n  /* material design spectification - xsmal/large = initial*/\n  /* min-width: 400px; */\n}\n\n\n.list-vertical-scroll {\n  max-height: calc(100vh - 192px);  /* minus header, margin, padding and another components height */\n  overflow-y: auto;\n}\n\n.truncate-ellipsed-parent {\n  min-width: 0;\n}\n\n.truncate-ellipsed {\n  white-space: nowrap;\n  text-overflow: ellipsis;\n  overflow: hidden;\n}\n\n.truncate-ellipsed-block {\n  display: block;\n  white-space: nowrap;\n  text-overflow: ellipsis;\n  overflow: hidden;\n}\n\n.avatar {\n  display: inline-block;\n\n  vertical-align: middle;\n  text-align: center;\n\n  /* make a square container */\n\n  /* background-size: 32px 32px; */\n\n  width: 32px;\n  height: 32px;\n\n  min-width: 32px;\n  min-height: 32px;\n /* line-height: 32px; */\n\n /*\n  min-width: 40px;\n  min-height: 40px;\n*/\n\n\n  /* fill the container, preserving aspect ratio, and cropping to fit */\n  background-size: cover;\n\n  /* center the image vertically and horizontally */\n\n  background-position: center;\n\n  /* baseado no left-icon no material_chip */\n/*\n  margin-top: 0px;\n  margin-bottom: 0px;\n  margin-left: -4px;\n  margin-right: 4px;\n  padding: 0px;\n*/\n\n  /* round the edges to a circle with border radius 1/2 container size */\n  border-radius: 50%;\n\n}\n\n.avatar-size {\n  display: inline-block;\n  vertical-align: middle;\n  text-align: center;\n\n  width: 40px;\n  height: 40px;\n  min-width: 40px;\n  min-height: 40px;\n}\n\n.avatar-border {\n  @extend .avatar;\n  /* border-style: dotted; */\n  border-style:  dashed;\n  /* border-width: thin; */\n  border-width: thin;\n  border-color: white;\n  /* mix-blend-mode: difference; */\n}\n\n.avatar-icon {\n\n  /* This image is 687 wide by 1024 tall, similar to your aspect ratio */\n  /* background-image: url(\'\'); */\n  display: inline-block;\n  vertical-align: middle;\n  text-align: center;\n\n  /* make a square container */\n  width: 24px;\n  height: 24px;\n  min-width: 24px;\n  min-height: 24px;\n\n  /* fill the container, preserving aspect ratio, and cropping to fit */\n  background-size: cover;\n\n  /* center the image vertically and horizontally */\n  background-position: center;\n\n  /* baseado no left-icon no material_chip */\n\n  /* round the edges to a circle with border radius 1/2 container size */\n  border-radius: 50%;\n\n}\n\n.text-group-letter {\n    color: white; /* foi preciso colocar, senão não funcionava o mix-blend-mode */\n    mix-blend-mode: difference;\n    line-height: 32px; /* same height to parent - class avatar*/\n}\n\n/* width */\n::-webkit-scrollbar {\n    width: 10px;\n}\n\n/* Track */\n::-webkit-scrollbar-track {\n    background: #f1f1f1;\n}\n\n/* Handle */\n::-webkit-scrollbar-thumb {\n     /* background: #888; */\n     /* background:  #82b1ff; */\n      background: #b3b3b3;\n}\n\n/* Handle on hover */\n::-webkit-scrollbar-thumb:hover {\n    /* background: #555; */\n    /* background: #2962ff; */\n    background: #808080;\n}\n\n/* To present graphic and background */\n/*\n@media print {\n    body {\n      -webkit-print-color-adjust: exact;\n    }\n}\n\n*/'];